import { Component, OnDestroy, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { GeneralApiService } from '../general-api.service';
import { User } from '../model/user';
import { first } from 'rxjs';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit, OnDestroy {
  form!: FormGroup;
  isSubmitted = false;
  accountNotFound = false;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private api: GeneralApiService,
    private loginService: LoginService
  ) {}

  ngOnDestroy(): void {
    throw new Error('Method not implemented.');
  }

  ngOnInit() {
    this.form = this.formBuilder.group({
      email: ['', [Validators.required, Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$")]],
      password: ['', Validators.required],
    });
  }

  get formEmail() {
    return this.form.controls['email'];
  }

  get formPassword() {
    return this.form.controls['password'];
  }

  onSubmit() {
    this.isSubmitted = true;

    if(this.form.invalid){
      return;
    }

    let user:User = {
      id: null,
      email: this.formEmail.value,
      password: this.formPassword.value
    }

    this.api.login(user)
    .pipe(first())
    .subscribe({
      next: data => {
        if(data.length === 0){
          this.accountNotFound = true;
        }
        else{
          this.accountNotFound = false;
          let token = data[0].token || "";
          this.loginService.LoginToken = token;
          this.router.n
        }

      },
      error: error => {


      },
      complete: () => {

      }
  })
  }
}
<div class="login-page">
    <div class="info">
        <h1 class="card-header">Welcome to UOL clinical data center</h1>
        <h4>here you can find health info of yourself</h4>
    </div>
    <div class="card login-form">
        <div class="card-body">
          <form [formGroup]="form" (ngSubmit)="onSubmit()">
            <div class="mb-3">
              <label class="form-label">Username</label>
              <input
                type="text"
                formControlName="email"
                class="form-control"
                [ngClass]="{ 'is-invalid': isSubmitted && formEmail.errors }"
              />
              <div
                *ngIf="isSubmitted && formEmail.errors"
                class="invalid-feedback"
              >
                <div *ngIf="isSubmitted && formEmail.errors['required']">
                  Email address is required
                </div>
                <div *ngIf="isSubmitted && formEmail.errors['pattern']">
                  This is not an proper email address
                </div>
              </div>
            </div>
            <div class="mb-3">
              <label class="form-label">Password</label>
              <input
                type="password"
                formControlName="password"
                class="form-control"
                [ngClass]="{ 'is-invalid': isSubmitted && formPassword.errors }"
              />
              <div
                *ngIf="isSubmitted && formPassword.errors"
                class="invalid-feedback"
              >
                <div *ngIf="isSubmitted && formPassword.errors['required']">
                  Password is required
                </div>
              </div>
            </div>
            <div class="submit-button">
              <button class="btn btn-primary">
                Login
              </button>
              <div class="invalid-submit" *ngIf="isSubmitted && accountNotFound">
                Please check if email and password matches
              </div>
            </div>
          </form>
        </div>
      </div>
</div>
