import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { User } from './model/user';
import { Observable, catchError, first, map, tap, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class GeneralApiService {

  private userUrl = 'api/users';
  private bloodPressureUrl = 'api/userBloodPressureData';


  constructor(private http: HttpClient) { }

  login(user: User) : Observable<User[]> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    //due to the limitation of mock api call, in real life I would not return all the users, but here I will filter out the correct user
    //in real life I will post the user email and password to backend, but please understand this is just a mock api call
    return this.http.get<User[]>(this.userUrl, {headers})
    .pipe(
      map(data => data.filter(each => each.email === user.email && each.password === user.password)),
      tap(data => console.log(JSON.stringify(data))),
      catchError(this.handleError)
    );
  }

  getUserBloodPressure(token: string): Observable<number[]> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
    const url = `${this.bloodPressureUrl}?token=${token}`;

    return this.http.get<number[]>(url, {headers})
    .pipe(
      map(data)
    )
  }

  private handleError(err: any) {
    // in a real world app, we may send the server to some remote logging infrastructure
    // instead of just logging it to the console
    let errorMessage: string;
    if (err.error instanceof ErrorEvent) {
      // A client-side or network error occurred. Handle it accordingly.
      errorMessage = `An error occurred: ${err.error.message}`;
    } else {
      // The backend returned an unsuccessful response code.
      // The response body may contain clues as to what went wrong,
      errorMessage = `Backend returned code ${err.status}: ${err.body.error}`;
    }
    console.error(err);
    return throwError(errorMessage);
  }
}
